import Unit from "../src/Unit";
import image from "../assets/Bear.png";
import GameContext from "./GameContext";
import imageA from "../assets/BearA.png";
import Slap from "../assets/slap1.wav";
export enum State {
    Attack = -1,
    Stop = 0,
    Walk = 1,
  }
  const sound = new Audio(Slap);
class Bear extends Unit
{
    public Pertenece;
    public range;
    public attack = 8;
    public xcoord;
    public life = 8000;
    public attackB = 800;
    public ycoord;
    public State: State =  State.Walk;
    private Bear = new Image();
    private BearA = new Image();
    //animation variables
    private padding;
    private frame = 0;
    private FrameCounter = 0;
    private xCutAnimation = 0;
    public speed = 5;
    public realx = 0;
 
    
    constructor()
    {
        super();
        this.Bear = new Image();
        this.Bear.src = image;
        this.BearA.src = imageA;
    }


    public render()
    {
        if(this.State == State.Walk){
            let sx = 0;
            let sy = 0;
            
            let sWidth = 64;
            let sHeight = 33;

            const context = GameContext.context;

            context.beginPath();
            context.save();
            if(this.Pertenece == 0)
            {
                context.scale(-1,1);
            }
            context.drawImage(this.Bear,sx + sWidth*this.frame,sy,sWidth,sHeight,this.xcoord,this.ycoord,200,100);
            
            context.restore();

            context.closePath();
        }
        else if(this.State == State.Attack){
            let sx = 0;
            let sy = 0;
            
            let sWidth = 74;
            let sHeight = 48;

            const context = GameContext.context;

            context.beginPath();
            context.save();
            if(this.Pertenece == 0)
            {
                context.scale(-1,1);
            }
            context.drawImage(this.BearA,sx + sWidth*this.frame,sy,sWidth,sHeight,this.xcoord,this.ycoord,225,150);
            
            context.restore();

            context.closePath();
        }
        else{
            let sx = 0;
            let sy = 0;
            
            let sWidth = 64;
            let sHeight = 33;

            const context = GameContext.context;

            context.beginPath();
            context.save();
            if(this.Pertenece == 0)
            {
                context.scale(-1,1);
            }
            context.drawImage(this.Bear,sx + sWidth*4,sy,sWidth,sHeight,this.xcoord,this.ycoord,200,100);
            
            context.restore();

            context.closePath();
        }

    }
    public update()
    {
        if(this.State == State.Walk){
            this.FrameCounter++;
            if(this.FrameCounter % 10 == 0){
                this.frame++;
            }
            if(this.frame > 4)
            {
                this.frame = 0;
            }
            
                this.xcoord -= this.speed;

                if(this.Pertenece == 1)
            {
                this.realx -= this.speed;
            }
            else
                this.realx += this.speed;
        }else if(this.State == State.Attack)
        {
            this.FrameCounter++;
            if(this.FrameCounter % 10 == 0){
                sound.play();
                this.frame++;
            }
            if(this.frame > 2)
            {
                this.frame = 0;
            }

        }else{

        }
        
        

        
    }

};
export default Bear;
